import React, { Component } from "react";
import "./addProductForm.css";

export class AddProductForm extends Component {

    constructor(){
        super();
        this.state = {
            name: '',
            brand: '',
            type: '',
            category: '',
            count: 0,
            price: 0,
            description: {},
            descriptionSize: 0,
        }
    }

    handleChange = (key, event) => {

        this.setState({
            [key]: event.target.value,
        })
    }


    addDescription = () => {
        this.setState(prev =>({
            descriptionSize: prev.descriptionSize + 1
        }))
    }

    addDescriptionKey = (key) => {
        this.setState(prevState => {
            let description = Object.assign({}, prevState.description);   
            description[key] = '';                    
            return { description };
        });
    }

    renderDescriptionForm = () => {

        return [...new Array(this.state.descriptionSize)].map((x,index) => {
            return (
                <div key={index} className="form-element">
                        <div className="input-div">
                             <input type="text" placeholder="KEY"/>
                        </div>
                        <div className="input-div">
                            <input type="text" placeholder="VALUE"/>
                        </div>
                </div>
            );
        });
    }

    render() {

        const { name, brand, type, category, count, price, description, descriptionSize } = this.state;

        return (
            <form className="add-product-form">
                <div className="form-element">
                    <div className="label-div">
                        <label for="name">Name:</label>
                    </div>
                    <div className="input-div">
                        <input type="text" name="name" value={name} onChange={(e) =>  {this.handleChange('name',e)}}/>
                    </div>
                </div>
                <div className="form-element">
                    <div className="label-div">
                        <label for="brand">Brand:</label>
                    </div>
                    <div className="input-div">
                        <input type="text" name="brand" value={brand} onChange={(e) =>  {this.handleChange('brand',e)}}/>
                    </div>
                </div>
                <div className="form-element">
                    <div className="label-div">
                        <label for="type">Type:</label>
                    </div>
                    <div className="input-div">
                        <input type="text" name="type" value={type} onChange={(e) =>  {this.handleChange('type',e)}}/>
                    </div>
                </div>
                <div className="description-element">
                    <h4 onClick={this.addDescription}>Add Details</h4>
                    <div className="body">
                        {
                            this.renderDescriptionForm()
                        }
                    </div>
                </div>
            </form>
        );
    }
}

export default AddProductForm;
